import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { fetchTransactions } from "../api";
import { Container, Typography, Button, Box, List, ListItem, ListItemText, Card, CardContent } from "@mui/material";

export default function Transactions() {
  const [transactions, setTransactions] = useState<any[]>([]);
  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (!storedUser) {
      navigate("/");
      return;
    }

    const user = JSON.parse(storedUser);

    fetchTransactions(user.id)
      .then(setTransactions)
      .catch((error) => console.error("Ошибка загрузки транзакций", error));
  }, [navigate]);

  return (
    <Container maxWidth="sm">
      <Box mt={5}>
        <Typography variant="h4">История транзакций</Typography>
        <Button variant="outlined" color="secondary" sx={{ mt: 3 }} onClick={() => navigate("/dashboard")}>
          Назад
        </Button>
        <List sx={{ mt: 3 }}>
          {transactions.length === 0 ? (
            <Typography>Нет транзакций</Typography>
          ) : (
            transactions.map((t) => (
              <Card key={t.id} sx={{ mt: 2 }}>
                <CardContent>
                  <ListItem>
                    <ListItemText
                      primary={`${t.user.name} → ${t.toUser}: $${t.amount}`}
                      secondary={t.timestamp}
                    />
                  </ListItem>
                </CardContent>
              </Card>
            ))
          )}
        </List>
      </Box>
    </Container>
  );
}
