import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
// import { fetchTransactions } from "../api";

export default function Transactions() {
  const [transactions, setTransactions] = useState<any[]>([]);
  const navigate = useNavigate();

//   useEffect(() => {
//     const storedUser = localStorage.getItem("user");
//     if (!storedUser) {
//       navigate("/");
//       return;
//     }

//     const user = JSON.parse(storedUser);

//     fetchTransactions(user.id)
//       .then(setTransactions)
//       .catch((error) => console.error("Ошибка загрузки транзакций", error));
//   }, [navigate]);

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold">История транзакций</h1>
      <button onClick={() => navigate("/dashboard")} className="bg-gray-500 text-white p-2 mt-4">
        Назад
      </button>
      <ul className="mt-4">
        {transactions.length === 0 ? <p>Нет транзакций</p> : transactions.map((t) => (
          <li key={t.id} className="border p-2 mt-2">
            <p><b>{t.user.name}</b> → <b>{t.toUser}</b>: ${t.amount}</p>
            <p className="text-sm text-gray-500">{t.timestamp}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}
