    //       return;
    //     }
    //     setUser(JSON.parse(storedUser));
    //   }, [navigate]);

    //   if (!user) return <Typography>Загрузка...</Typography>;

    return (
        <Container maxWidth="sm">
            <Box mt={5}>
                <Typography variant="h4">Здравствуйте, {user.name}!</Typography>
                <Card sx={{ mt: 3 }}>
                    <CardContent>
                        <Typography variant="h6">Ваш баланс:</Typography>
                        <Typography variant="h4">${user.balance}</Typography>
                    </CardContent>
                </Card>
                <Button variant="contained" color="primary" sx={{ mt: 3, mr: 2 }} onClick={() => navigate("/transfer")}>
                    Сделать перевод
                </Button>
                <Button variant="outlined" color="secondary" sx={{ mt: 3 }} onClick={() => navigate("/transactions")}>
                    История транзакций
                </Button>
            </Box>
        </Container>
    );
}
