import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { transferMoney } from "../api";
import { Container, TextField, Button, Typography, Box, Alert } from "@mui/material";

export default function Transfer() {
  const [to, setTo] = useState("");
  const [amount, setAmount] = useState(0);
  const [error, setError] = useState<string | null>(null);
  const navigate = useNavigate();

  const handleTransfer = async () => {
    const storedUser = localStorage.getItem("user");
    if (!storedUser) {
      navigate("/");
      return;
    }

    const user = JSON.parse(storedUser);

    try {
      await transferMoney(user.name, to, amount);
      navigate("/dashboard");
    } catch (error) {
      setError("Ошибка перевода");
    }
  };

  return (
    <Container maxWidth="sm">
      <Box mt={5}>
        <Typography variant="h4">Перевод денег</Typography>
        {error && <Alert severity="error" sx={{ mt: 2 }}>{error}</Alert>}
        <TextField
          label="Кому"
          fullWidth
          margin="normal"
          value={to}
          onChange={(e) => setTo(e.target.value)}
        />
        <TextField
          label="Сумма"
          type="number"
          fullWidth
          margin="normal"
          value={amount}
          onChange={(e) => setAmount(Number(e.target.value))}
        />
        <Button variant="contained" color="primary" sx={{ mt: 3 }} onClick={handleTransfer}>
          Отправить
        </Button>
      </Box>
    </Container>
  );
}
